import java.io.BufferedOutputStream;
import java.io.BufferedReader;
import java.io.File;
import java.io.FileInputStream;
import java.io.FileNotFoundException;
import java.io.IOException;
import java.io.InputStreamReader;
import java.io.OutputStream;
import java.io.PrintWriter;
import java.net.ServerSocket;
import java.net.Socket;
import java.net.URLDecoder;
import java.net.URLEncoder;
import java.nio.charset.StandardCharsets;
import java.util.Date;
import java.util.StringTokenizer;

// Modified by Prof. P. Filipe - 22/05/2021
// https://www.ssaurel.com/blog/create-a-simple-http-web-server-in-java
// Each Client Connection will be managed in a dedicated Thread
public class ServidorHTTP implements Runnable {
	// verbose mode
	static final boolean verbose = true;
	// port to listen connection
	static final int PORT = 80;
	static final String WEB_ROOT = "src/main/webapp";// "WebContent";
	static final String Sentinela = "<!-- lista de recursos -->";
	static String listaRecursos = null; // página gerada com os recursos disponiveis
	static final String indice = "<!DOCTYPE html>\r\n" + "<html>\r\n" + "<head>\r\n"
			+ "<meta http-equiv=\"Content-Type\" content=\"text/html\">\r\n"
			+ "<meta http-equiv=\"Content-Language\" content=\"pt\">\r\n"
			+ "<title>Índice de Recursos do Servidor HTTP</title>\r\n"
			+ "<link href='http://fonts.googleapis.com/css?family=Great+Vibes'\r\n"
			+ "	rel='stylesheet' type='text/css'>\r\n" + "<style>\r\n" + "h2 {\r\n" + "	display: block;\r\n"
			+ "	margin-top: 1%;\r\n" + "	margin-bottom: 1%;\r\n" + "	margin-left: 10%;\r\n"
			+ "	margin-right: 10%;\r\n" + "	font: 400 30px/0.8 'Great Vibes', Helvetica, sans-serif;\r\n"
			+ "	text-shadow: 4px 4px 3px rgba(0, 0, 0, 0.1);\r\n" + "	text-align: justify;\r\n"
			+ "	text-justify: inter-word;\r\n" + "	line-height: 1.5;\r\n" + "	opacity: 0.9;\r\n"
			+ "	color: DarkGoldenRod;\r\n" + "}\r\n" + "</style>\r\n" + "</head>\r\n" + "<body>\r\n" + "	<table>\r\n"
			+ "		<tr>\r\n"
			+ "			<td><img src=\"data:image/jpeg;base64,\"\r\n"
			+ "				alt=\"Falhou a apresentação do Logotipo do ISEL\"\r\n"
			+ "				title=\"Logotipo do ISEL\" style=\"width: 118px; height: 103px;\">\r\n"
			+ "			</td>\r\n" + "			<td>\r\n" + "				<p\r\n"
			+ "					style=\"font: 400 30px/0.8 'Great Vibes', Helvetica, sans-serif; color: #9a3324;\">Lista\r\n"
			+ "					de Recursos</p>\r\n" + "			</td>\r\n" + "		</tr>\r\n" + "	</table>\r\n"
			+ "	<!-- lista de recursos -->\r\n" + "</body>\r\n" + "</html>";

	static final String DEFAULT_FILE = "index.html";
	static final String FILE_NOT_FOUND = "404.html";
	static final String METHOD_NOT_SUPPORTED = "error.htm";

	// Client Connection via Socket Class
	private Socket connect;

	public ServidorHTTP(Socket c) {
		connect = c;
	}

	public static void main(String[] args) {
		try {
			@SuppressWarnings("resource")
			ServerSocket serverConnect = new ServerSocket(PORT);
			System.out.println("Server started.\nListening for connections on port : " + PORT + " ...\n");

			// we listen until user halts server execution
			while (true) {
				ServidorHTTP myServer = new ServidorHTTP(serverConnect.accept());

				if (verbose) {
					System.out.println("Connection opened. (" + new Date() + ")");
				}

				// create dedicated thread to manage the client connection
				Thread thread = new Thread(myServer);
				thread.start();
			}

		} catch (IOException e) {
			System.err.println("Server Connection error : " + e.getMessage());
		}
	}

	@Override
	public void run() {
		// we manage our particular client connection
		BufferedReader in = null;
		PrintWriter out = null;
		BufferedOutputStream dataOut = null;
		String fileRequested = null;

		try {
			// we read characters from the client via input stream on the socket
			in = new BufferedReader(new InputStreamReader(connect.getInputStream()));
			// we get character output stream to client (for headers)
			out = new PrintWriter(connect.getOutputStream());
			// get binary output stream to client (for requested data)
			dataOut = new BufferedOutputStream(connect.getOutputStream());

			// get first line of the request from the client
			String input = in.readLine();
			if (input != null) {
				// we parse the request with a string tokenizer
				StringTokenizer parse = new StringTokenizer(input);
				// we get the HTTP method of the client
				String method = parse.nextToken().toUpperCase();
				// we get file requested
				fileRequested = parse.nextToken().toLowerCase();

				// we support only GET and HEAD methods, we check
				if (!method.equals("GET") && !method.equals("HEAD")) {
					if (verbose) {
						System.out.println("501 Not Implemented : " + method + " method.");
					}

					// we return the not supported file to the client
					File file = new File(WEB_ROOT, METHOD_NOT_SUPPORTED);
					int fileLength = (int) file.length();
					String contentMimeType = "text/html";

					// read content to return to client
					byte[] fileData = readFileData(file, fileLength);

					// we send HTTP Headers with data to client
					out.println("HTTP/1.1 501 Not Implemented");
					out.println("Server: Java HTTP Server: 1.0");
					out.println("Date: " + new Date());
					out.println("Content-type: " + contentMimeType);
					out.println("Content-length: " + fileLength);
					out.println(); // blank line between headers and content, very important !
					out.flush(); // flush character output stream buffer
					// file
					dataOut.write(fileData, 0, fileLength);
					dataOut.flush();

				} else {
					// GET or HEAD method
					byte[] fileData = null;
					String content = null;
					if (fileRequested.endsWith("/")) {
						fileRequested += DEFAULT_FILE; 
						if (listaRecursos == null)	// gera o indice
							listaRecursos = indice.replace(Sentinela, getIndice());
						content = "text/html";
						fileData = listaRecursos.getBytes();
					} else {
						File file = new File(WEB_ROOT, URLDecoder.decode(fileRequested,StandardCharsets.UTF_8));

						int fileLength = (int) file.length();

						content = getContentType(fileRequested);

						fileData = readFileData(file, fileLength);
					}
					// send HTTP Headers
					out.println("HTTP/1.1 200 OK");
					out.println("Server: Java HTTP: 1.0");
					out.println("Date: " + new Date());
					out.println("Content-type: " + content);
					out.println("Content-length: " + fileData.length);
					out.println(); // blank line between headers and content, very important !
					out.flush(); // flush character output stream buffer

					if (method.equals("GET")) { // GET method so we return content
						dataOut.write(fileData);
						dataOut.flush();
					}

					if (verbose) {
						System.out.println("File '" + fileRequested + "' of type '" + content + "' returned!");
					}
				}
			}
		} catch (FileNotFoundException fnfe) {
			try {
				fileNotFound(out, dataOut, fileRequested);
			} catch (IOException ioe) {
				System.err.println("Error with file not found exception: " + ioe.getMessage());
			}

		} catch (IOException ioe) {
			System.err.println("Server error: " + ioe);
		} finally {
			try {
				in.close();
				out.close();
				dataOut.close();
				connect.close(); // we close socket connection
				if (verbose) {
					System.out.println("Connection closed!");
				}
			} catch (Exception e) {
				System.err.println("Error closing stream: " + e.getMessage());
			}
		}
	}

	private byte[] readFileData(File file, int fileLength) throws IOException {
		FileInputStream fileIn = null;
		byte[] fileData = new byte[fileLength];

		try {
			fileIn = new FileInputStream(file);
			fileIn.read(fileData);
		} finally {
			if (fileIn != null)
				fileIn.close();
		}
		return fileData;
	}

	// return supported MIME Types
	private String getContentType(String fileRequested) {
		if (fileRequested.endsWith(".htm") || fileRequested.endsWith(".html"))
			return "text/html";
		if (fileRequested.endsWith(".xml") || fileRequested.endsWith(".xsl") || fileRequested.endsWith(".xsd"))
			return "text/xml";
		if (fileRequested.endsWith(".jpg") || fileRequested.endsWith(".jpeg"))
			return "image/jpeg";
		if (fileRequested.endsWith(".png"))
			return "image/png";
		if (fileRequested.endsWith(".ico"))
			return "image/x-icon";
		if (fileRequested.endsWith(".mp4"))
			return "video/mp4";
		return "text/plain";
	}

	private void fileNotFound(PrintWriter out, OutputStream dataOut, String fileRequested) throws IOException {
		File file = new File(WEB_ROOT, FILE_NOT_FOUND);
		int fileLength = (int) file.length();
		String content = "text/html";
		byte[] fileData = readFileData(file, fileLength);

		out.println("HTTP/1.1 404 File Not Found");
		out.println("Server: Java HTTP Server: 1.0");
		out.println("Date: " + new Date());
		out.println("Content-type: " + content);
		out.println("Content-length: " + fileLength);
		out.println(); // blank line between headers and content, very important !
		out.flush(); // flush character output stream buffer

		dataOut.write(fileData, 0, fileLength);
		dataOut.flush();

		if (verbose) {
			System.out.println("File " + fileRequested + " not found");
		}
	}

	private static String[] getFiles(final String pasta) {
		int j = 0;
		File folder = new File(pasta);
		File[] listOfFiles = folder.listFiles();
		for (int i = 0; i < listOfFiles.length; i++)
			if (listOfFiles[i].isFile())
				j = j + 1;
		String[] files = new String[j];
		j = 0;
		for (int i = 0; i < listOfFiles.length; i++)
			if (listOfFiles[i].isFile())
				files[j++] = listOfFiles[i].getName();
		return files;
	}

	private static String getIndice() {
		String[] recursos = getFiles(WEB_ROOT + "/recursos");
		String strLinks = "<ol>";
		for (int i = 0; i < recursos.length; i++) {
			String Dsg = recursos[i].replaceAll("_", " ");
			Dsg = Dsg.substring(0,Dsg.lastIndexOf("."));
			strLinks = strLinks + "<li><a href=\"recursos/" +  URLEncoder.encode(recursos[i],StandardCharsets.UTF_8) + "\">" + Dsg
					+ "</a></li>\n";
		}
		return strLinks + "\r\n</ol><h2>Lista atualizada em: " + new java.util.Date() + "</h2>\n";
	}

}